mapper.select:
SELECT COUNT(*) FROM SGROUP_MEMBER
WHERE SGROUP_MEM_ID =
#{memberId}

SELECT * FROM SGROUP
JOIN SGROUP_MEMBER USING (SGROUP_ID)
WHERE SGROUP_ID = ${groupId}

mapper.update:
UPDATE SGROUP SET
SGROUP_NAME = #{sgroupName}, SGROUP_OPEN = #{sgroupOpen}, SGROUP_PWD =
#{sgroupPwd},
SGROUP_EXPLAIN = #{sgroupEx}
WHERE SGROUP_ID = #{sgroupId}
